import React, { useState, useEffect } from 'react';
import axios from 'axios';

import EmpresascCSS from "../Empresas/Empresas.css"
import MenuLateral from "../../Components/MenuLateral";
import Header from "../../Components/Header";
import Footer from "../../Components/Footer";


export default function Empresas(){
    const [ ListarEmpresas, setListarEmpresas ] = useState( [] );
    const [ isLoading, setIsLoading ] = useState( false );


    function buscarEmpresas(){
        setIsLoading( true );
        // Faz a chamada para a API usando axios
        axios('http://localhost:5000/api/empresas', {
            headers : {
                'Authorization' : 'Bearer ' + localStorage.getItem('usuario-login')
            }
        })

        // Caso a resposta da requisição retorne um status code 200
        .then(resposta => {
            if (resposta.status === 200) {
                // Chama a função que atualiza o state listaTiposUsuarios
                setListarEmpresas(resposta.data);
                setIsLoading( false );
            };
        })

        // Mostra no console do navegador
        .catch(error => console.log(error));
    };

    return (
        <div>
            <Header/>
            <MenuLateral/>
            <main className='conteudoPrincipal'>
                <section className='conteudoPrincipal-cadastro'>
                    <h1>Empresas</h1>
                    <div>
                        <input type="share" />
                        <button>
                            <img src="" alt="Cadastrar nova Empresa" />
                            Nova Empresa
                        </button>
                    </div>
                </section>

                <section className='sec-container'> 
                    <div className="div-container" id="conteudoPrincipal-lista">
                        <table id="tabela-lista">
                            <thead>
                                <tr>
                                    <th>Nome</th>
                                    <th>Contato</th>
                                    <th>Editar</th>
                                    <th>Ver no mapa</th>
                                </tr>
                                
                            </thead>
                            <tbody id="tabela-lista-corpo">
                                    {
                                        ListarEmpresas.map( (empresa) => {
                                            return(
                                                <tr key={empresa.idEmpresa}>
                                                    <td>{empresa.nomeEmpresa}</td>
                                                    <td>{empresa.contato}</td>
                                                </tr>
                                            );
                                        } )
                                    } 
                            </tbody>                  
                        </table>
                    </div>
                </section>
            </main>
            <Footer/>
        </div>
    )
};
