import React, { useState, useEffect, Component } from 'react'
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import axios from 'axios';


var listaLugares = []
const containerStyle = {
  width: '800px',
  height: '550px'
};

const center = {
  lat: -3.745,
  lng: -38.523
};

const center2 = {
  lat: -23.536420979015343, 
  lng: -46.64624308623364
};

const center3 = {
  lat: 0,
  lng: 0
};

function montarMapa(itens) {
  var map = new google.maps.Map(document.getElementById("map"), {
      zoom : 10,
      center : new google.maps.LatLng(-23.53642760296254, -46.64621432441258),
      mapTypeId : google.maps.MapTypeId.ROADMAP
  });

  var infoWindow = new google.maps.InfoWindow();

  var marker, i;

  for(i = 0; i < itens.length; i++) {
      console.log(itens[i].latitude);
      marker = new google.maps.Marker({
          position : new google.maps.LatLng(
              itens[i].latitude,
              itens[i].longitude
          ),
          map : map
      });

      google.maps.event.addListener(
          marker,
          "click",
          (function(marker, i) {
              return function () {
                  infoWindow.setContent(itens[i].id);
                  infoWindow.open(map, marker);
              };
          })(marker, i)
      )};            
}

function ListarLugares(){
  axios('https://623d99bee8fbc4f16264b76d.mockapi.io/Lugares', {

  }).then(resposta => {
    if(resposta.status = 200){
      listaLugares = (resposta.data);
      return console.console.log(listaLugares);
    }
  })
  .catch(erro => console.log(erro))
}

function MyComponent() {

  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "AIzaSyCfPgkys_hqQcfea7wLFl9_uZKYsGoHSek"
  })

  const [map, setMap] = React.useState(null)

  const onLoad = React.useCallback(function callback(map) {
    const bounds = new window.google.maps.LatLngBounds();
    map.fitBounds(bounds);
    setMap(map)
  }, [])

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null)
  }, [])

  return isLoaded ? (
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={center}
        zoom={10}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        { /* Child components, such as markers, info windows, etc. */ }
        {
        ListarLugares,
        console.log(listaLugares),
        listaLugares.forEach(element => {
          center3 = {
            lat: element.lat,
            lng: element.lng
          };
        })
      }
        <Marker position={center}/>
        <Marker position={center2}/>
        <>
        </>
      </GoogleMap>
  ) : <></>
}

export default MyComponent
